/**
 * API Service Module
 * Contains all API methods for communicating with backend
 * Uses the configured axios instance from api.js
 * Currently uses mock authentication and would be replaced with real API calls
 */

import api from './api'

export default {
  /**
   * Authentication Methods
   */
  
  /**
   * Mock login method - simulates authentication
   * In production, this would call a real authentication endpoint
   * @param {Object} credentials - User credentials { email, password }
   * @returns {Promise} Promise resolving to token and user data
   */
  login(credentials) {
    return new Promise((resolve) => {
      // Simulate network delay
      setTimeout(() => {
        if (credentials.email && credentials.password) {
          resolve({
            data: {
              // Generate mock JWT token with timestamp
              token: 'mock-jwt-token-' + Date.now(),
              user: {
                id: 1,
                // Extract username from email
                name: credentials.email.split('@')[0],
                email: credentials.email
              }
            }
          })
        }
      }, 500)
    })
  },

  /**
   * Project API Methods
   * In production, these would make real HTTP requests to backend
   */
  
  /**
   * Fetch all projects
   * @returns {Promise} Promise resolving to array of projects
   */
  getProjects() {
    return api.get('/projects')
  },

  /**
   * Fetch single project by ID
   * @param {number|string} id - Project ID
   * @returns {Promise} Promise resolving to project object
   */
  getProject(id) {
    return api.get(`/projects/${id}`)
  },

  /**
   * Create new project
   * @param {Object} project - Project data
   * @returns {Promise} Promise resolving to created project
   */
  createProject(project) {
    return api.post('/projects', project)
  },

  /**
   * Update existing project
   * @param {number|string} id - Project ID
   * @param {Object} project - Updated project data
   * @returns {Promise} Promise resolving to updated project
   */
  updateProject(id, project) {
    return api.put(`/projects/${id}`, project)
  },

  /**
   * Delete project
   * @param {number|string} id - Project ID
   * @returns {Promise} Promise resolving to deletion confirmation
   */
  deleteProject(id) {
    return api.delete(`/projects/${id}`)
  },

  /**
   * Task API Methods
   * Nested under projects as tasks belong to projects
   */
  
  /**
   * Fetch all tasks for a project
   * @param {number|string} projectId - Project ID
   * @returns {Promise} Promise resolving to array of tasks
   */
  getTasks(projectId) {
    return api.get(`/projects/${projectId}/tasks`)
  },

  /**
   * Create new task in a project
   * @param {number|string} projectId - Project ID
   * @param {Object} task - Task data
   * @returns {Promise} Promise resolving to created task
   */
  createTask(projectId, task) {
    return api.post(`/projects/${projectId}/tasks`, task)
  },

  /**
   * Update existing task
   * @param {number|string} projectId - Project ID
   * @param {number|string} taskId - Task ID
   * @param {Object} task - Updated task data
   * @returns {Promise} Promise resolving to updated task
   */
  updateTask(projectId, taskId, task) {
    return api.put(`/projects/${projectId}/tasks/${taskId}`, task)
  },

  /**
   * Delete task
   * @param {number|string} projectId - Project ID
   * @param {number|string} taskId - Task ID
   * @returns {Promise} Promise resolving to deletion confirmation
   */
  deleteTask(projectId, taskId) {
    return api.delete(`/projects/${projectId}/tasks/${taskId}`)
  }
}
