/**
 * AUTH.JS - Authentication Module
 * 
 * Manages:
 * - User login/logout
 * - Auth token storage
 * - User data (name, email)
 * - Session persistence (localStorage keeps you logged in after refresh)
 */

const state = {
  // JWT token: proves user is authenticated (stored locally to survive refresh)
  token: localStorage.getItem('auth_token') || null,
  // Flag: quickly check if user is logged in
  isAuthenticated: !!localStorage.getItem('auth_token'),
  // User info: { id, name, email } - stored locally
  user: JSON.parse(localStorage.getItem('user') || 'null')
}

/**
 * Getters: Read-only access to state
 * Used in components to get current auth info
 */
const getters = {
  // Is user logged in?
  isAuthenticated: state => state.isAuthenticated,
  // Get the auth token
  getToken: state => state.token,
  // Get user object
  getUser: state => state.user
}

/**
 * Mutations: Change state (always synchronous)
 * Only way to modify state - keeps it predictable
 */
const mutations = {
  // Save token to state and localStorage
  SET_TOKEN(state, token) {
    state.token = token
    state.isAuthenticated = !!token
    if (token) {
      localStorage.setItem('auth_token', token)
    } else {
      localStorage.removeItem('auth_token')
    }
  },
  
  // Save user info to state and localStorage
  SET_USER(state, user) {
    state.user = user
    if (user) {
      localStorage.setItem('user', JSON.stringify(user))
    } else {
      localStorage.removeItem('user')
    }
  },
  
  // Clear everything on logout
  LOGOUT(state) {
    state.token = null
    state.isAuthenticated = false
    state.user = null
    localStorage.removeItem('auth_token')
    localStorage.removeItem('user')
  }
}

const actions = {
  // Save login data: token and user info
  login({ commit }, { token, user }) {
    commit('SET_TOKEN', token)
    commit('SET_USER', user)
  },
  
  // Logout: clear everything and redirect
  logout({ commit }) {
    commit('LOGOUT')
  }
}

/**
 * Export Auth Module
 * namespaced: true - Module is namespaced (auth/getters, auth/actions, etc.)
 */
export default {
  namespaced: true,
  state,
  getters,
  mutations,
  actions
}
