/**
 * ROUTER/INDEX.JS - Page Navigation Setup
 * 
 * Defines all app routes and their permissions:
 * - Which pages require login
 * - Which pages anyone can access
 * - Auto-redirects users based on auth status
 */

import Vue from 'vue';
import VueRouter from 'vue-router';
import store from '../store';
import Login from '../views/Login.vue';
import Register from '../views/Register.vue';
import Dashboard from '../views/Dashboard.vue';
import ProjectsList from '../views/ProjectsList.vue';
import ProjectDetail from '../views/ProjectDetail.vue';
import TasksView from '../views/TasksView.vue';
import NotFound from '../views/NotFound.vue';
Vue.use(VueRouter);

/**
 * Route List: All pages in the app
 * Each route has:
 * - path: URL path (/login, /dashboard, etc)
 * - component: Vue component to show
 * - meta.requiresAuth: true = login required, false = anyone can access
 */
const routes = [{
  path: '/',
  redirect: '/login'
}, {
  path: '/login',
  name: 'Login',
  component: Login,
  meta: {
    requiresAuth: false
  }
}, {
  path: '/register',
  name: 'Register',
  component: Register,
  meta: {
    requiresAuth: false
  }
}, {
  path: '/dashboard',
  name: 'Dashboard',
  component: Dashboard,
  meta: {
    requiresAuth: true
  }
}, {
  path: '/projects',
  name: 'ProjectsList',
  component: ProjectsList,
  meta: {
    requiresAuth: true
  }
}, {
  path: '/projects/:id',
  name: 'ProjectDetail',
  component: ProjectDetail,
  meta: {
    requiresAuth: true
  }
}, {
  path: '/projects/:id/tasks',
  name: 'TasksView',
  component: TasksView,
  meta: {
    requiresAuth: true
  }
}, {
  path: '*',
  name: 'NotFound',
  component: NotFound,
  meta: {
    requiresAuth: false
  }
}];
const router = new VueRouter({
  mode: 'history',
  // Clean URLs without #
  base: process.env.BASE_URL,
  routes
});

/**
 * Navigation Guard: Check permissions before showing a page
 * - If page needs login but user isn't logged in → redirect to login
 * - Otherwise → allow access
 */
router.beforeEach((to, from, next) => {
  const isAuthenticated = store.getters['auth/isAuthenticated'];
  const requiresAuth = to.matched.some(record => record.meta.requiresAuth);
  if (requiresAuth && !isAuthenticated) {
    // Need login but not logged in → go to login page
    next('/login');
  } else {
    // Allow access
    next();
  }
});
export default router;
