import { Doughnut } from 'vue-chartjs';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
ChartJS.register(ArcElement, Tooltip, Legend);
export default {
  name: 'TaskStatusChart',
  extends: Doughnut,
  props: {
    tasks: {
      type: Array,
      default: () => []
    }
  },
  mounted() {
    this.renderChart(this.chartData, this.chartOptions);
  },
  computed: {
    chartData() {
      const statusCounts = {
        'To Do': 0,
        'In Progress': 0,
        'Done': 0
      };
      this.tasks.forEach(task => {
        if (statusCounts[task.status] !== undefined) {
          statusCounts[task.status]++;
        }
      });
      return {
        labels: ['To Do', 'In Progress', 'Done'],
        datasets: [{
          label: 'Tasks by Status',
          data: [statusCounts['To Do'], statusCounts['In Progress'], statusCounts['Done']],
          backgroundColor: ['rgba(255, 209, 220, 0.8)',
          // Pastel pink
          'rgba(255, 229, 212, 0.8)',
          // Pastel peach
          'rgba(193, 240, 220, 0.8)' // Pastel mint
          ],
          borderColor: ['rgba(255, 209, 220, 1)', 'rgba(255, 229, 212, 1)', 'rgba(193, 240, 220, 1)'],
          borderWidth: 2
        }]
      };
    },
    chartOptions() {
      return {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
          legend: {
            position: 'bottom',
            labels: {
              padding: 15,
              font: {
                size: 12,
                family: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif"
              },
              usePointStyle: true,
              pointStyle: 'circle'
            }
          },
          tooltip: {
            backgroundColor: 'rgba(255, 255, 255, 0.9)',
            titleColor: '#333',
            bodyColor: '#666',
            borderColor: '#D4C5F9',
            borderWidth: 1,
            padding: 12,
            cornerRadius: 8,
            titleFont: {
              size: 14,
              weight: 'bold'
            },
            bodyFont: {
              size: 13
            }
          }
        },
        cutout: '60%'
      };
    }
  },
  watch: {
    tasks: {
      deep: true,
      handler() {
        this.renderChart(this.chartData, this.chartOptions);
      }
    }
  }
};
