import Vue from 'vue'
import VueRouter from 'vue-router'
import store from '../store'
// Import view components
import Login from '../views/Login.vue'
import Register from '../views/Register.vue'
import Dashboard from '../views/Dashboard.vue'
import ProjectsList from '../views/ProjectsList.vue'
import ProjectDetail from '../views/ProjectDetail.vue'
import TasksView from '../views/TasksView.vue'
import NotFound from '../views/NotFound.vue'

Vue.use(VueRouter)

/**
 * Route Definitions
 * Each route has:
 * - path: URL path
 * - name: Route name for programmatic navigation
 * - component: Vue component to render
 * - meta: Custom data (requiresAuth flag)
 */
const routes = [
  {
    // Root path redirects to dashboard
    path: '/',
    redirect: '/dashboard'
  },
  {
    // Login page - accessible without authentication
    path: '/login',
    name: 'Login',
    component: Login,
    meta: { requiresAuth: false }
  },
  {
    // Registration page - accessible without authentication
    path: '/register',
    name: 'Register',
    component: Register,
    meta: { requiresAuth: false }
  },
  {
    // Dashboard - shows project statistics
    path: '/dashboard',
    name: 'Dashboard',
    component: Dashboard,
    meta: { requiresAuth: true }
  },
  {
    // Projects list - shows all projects
    path: '/projects',
    name: 'ProjectsList',
    component: ProjectsList,
    meta: { requiresAuth: true }
  },
  {
    // Project detail - shows single project with tasks preview
    // :id is a route parameter (project ID)
    path: '/projects/:id',
    name: 'ProjectDetail',
    component: ProjectDetail,
    meta: { requiresAuth: true }
  },
  {
    // Tasks view - nested route for managing project tasks
    // :id is the project ID
    path: '/projects/:id/tasks',
    name: 'TasksView',
    component: TasksView,
    meta: { requiresAuth: true }
  }
  ,
  {
    // Catch-all route for undefined paths
    path: '*',
    name: 'NotFound',
    component: NotFound,
    meta: { requiresAuth: false }
  }
]

/**
 * Create router instance
 * - mode: 'history' uses HTML5 History API (no # in URLs)
 * - base: Base URL for the app
 * - routes: Array of route configurations
 */
const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

/**
 * Global Navigation Guard (beforeEach)
 * Runs before every route navigation
 * Implements authentication logic:
 * - Protected routes require authentication
 * - Unauthenticated users are redirected to login
 * - Authenticated users trying to access login are redirected to dashboard
 * 
 * @param {Object} to - Target route
 * @param {Object} from - Current route
 * @param {Function} next - Function to proceed with navigation
 */
router.beforeEach((to, from, next) => {
  // Check if user is authenticated (from Vuex store)
  const isAuthenticated = store.getters['auth/isAuthenticated']
  
  // Check if target route requires authentication
  const requiresAuth = to.matched.some(record => record.meta.requiresAuth)

  if (requiresAuth && !isAuthenticated) {
    // Protected route + not authenticated → redirect to login
    next('/login')
  } else if (to.path === '/login' && isAuthenticated) {
    // Login page + already authenticated → redirect to dashboard
    next('/dashboard')
  } else {
    // Allow navigation
    next()
  }
})

export default router
