const state = {
  projects: [],
  activeProject: null
}

const getters = {
  allProjects: state => state.projects,
  activeProject: state => state.activeProject,
  projectCount: state => state.projects.length,
  completedTaskCount: state => {
    if (!state.activeProject || !state.activeProject.tasks) return 0
    return state.activeProject.tasks.filter(task => task.status === 'Done').length
  },
  getProjectById: state => id => {
    return state.projects.find(project => project.id === id)
  }
}

const mutations = {
  SET_PROJECTS(state, projects) {
    state.projects = projects
  },
  ADD_PROJECT(state, project) {
    state.projects.push(project)
  },
  UPDATE_PROJECT(state, updatedProject) {
    const index = state.projects.findIndex(p => p.id === updatedProject.id)
    if (index !== -1) {
      state.projects.splice(index, 1, updatedProject)
    }
  },
  DELETE_PROJECT(state, projectId) {
    state.projects = state.projects.filter(p => p.id !== projectId)
  },
  SET_ACTIVE_PROJECT(state, project) {
    state.activeProject = project
  },
  ADD_TASK_TO_PROJECT(state, { projectId, task }) {
    const project = state.projects.find(p => p.id === projectId)
    if (project) {
      if (!project.tasks) {
        project.tasks = []
      }
      project.tasks.push(task)
      if (state.activeProject && state.activeProject.id === projectId) {
        state.activeProject = { ...project }
      }
    }
  },
  UPDATE_TASK_IN_PROJECT(state, { projectId, task }) {
    const project = state.projects.find(p => p.id === projectId)
    if (project && project.tasks) {
      const taskIndex = project.tasks.findIndex(t => t.id === task.id)
      if (taskIndex !== -1) {
        project.tasks.splice(taskIndex, 1, task)
        if (state.activeProject && state.activeProject.id === projectId) {
          state.activeProject = { ...project }
        }
      }
    }
  },
  DELETE_TASK_FROM_PROJECT(state, { projectId, taskId }) {
    const project = state.projects.find(p => p.id === projectId)
    if (project && project.tasks) {
      project.tasks = project.tasks.filter(t => t.id !== taskId)
      if (state.activeProject && state.activeProject.id === projectId) {
        state.activeProject = { ...project }
      }
    }
  }
}

const actions = {
  async fetchProjects({ commit }) {
    const mockProjects = JSON.parse(localStorage.getItem('projects') || '[]')
    commit('SET_PROJECTS', mockProjects)
  },
  addProject({ commit }, project) {
    commit('ADD_PROJECT', project)
    const projects = JSON.parse(localStorage.getItem('projects') || '[]')
    projects.push(project)
    localStorage.setItem('projects', JSON.stringify(projects))
  },
  updateProject({ commit }, project) {
    commit('UPDATE_PROJECT', project)
    const projects = JSON.parse(localStorage.getItem('projects') || '[]')
    const index = projects.findIndex(p => p.id === project.id)
    if (index !== -1) {
      projects[index] = project
      localStorage.setItem('projects', JSON.stringify(projects))
    }
  },
  deleteProject({ commit }, projectId) {
    commit('DELETE_PROJECT', projectId)
    const projects = JSON.parse(localStorage.getItem('projects') || '[]')
    const filtered = projects.filter(p => p.id !== projectId)
    localStorage.setItem('projects', JSON.stringify(filtered))
  },
  setActiveProject({ commit, state }, projectId) {
    const project = state.projects.find(p => p.id === projectId)
    commit('SET_ACTIVE_PROJECT', project)
  },
  addTask({ commit }, { projectId, task }) {
    commit('ADD_TASK_TO_PROJECT', { projectId, task })
    const projects = JSON.parse(localStorage.getItem('projects') || '[]')
    const projectIndex = projects.findIndex(p => p.id === projectId)
    if (projectIndex !== -1) {
      if (!projects[projectIndex].tasks) {
        projects[projectIndex].tasks = []
      }
      projects[projectIndex].tasks.push(task)
      localStorage.setItem('projects', JSON.stringify(projects))
    }
  },
  updateTask({ commit }, { projectId, task }) {
    commit('UPDATE_TASK_IN_PROJECT', { projectId, task })
    const projects = JSON.parse(localStorage.getItem('projects') || '[]')
    const projectIndex = projects.findIndex(p => p.id === projectId)
    if (projectIndex !== -1 && projects[projectIndex].tasks) {
      const taskIndex = projects[projectIndex].tasks.findIndex(t => t.id === task.id)
      if (taskIndex !== -1) {
        projects[projectIndex].tasks[taskIndex] = task
        localStorage.setItem('projects', JSON.stringify(projects))
      }
    }
  },
  deleteTask({ commit }, { projectId, taskId }) {
    commit('DELETE_TASK_FROM_PROJECT', { projectId, taskId })
    const projects = JSON.parse(localStorage.getItem('projects') || '[]')
    const projectIndex = projects.findIndex(p => p.id === projectId)
    if (projectIndex !== -1 && projects[projectIndex].tasks) {
      projects[projectIndex].tasks = projects[projectIndex].tasks.filter(t => t.id !== taskId)
      localStorage.setItem('projects', JSON.stringify(projects))
    }
  }
}

export default {
  namespaced: true,
  state,
  getters,
  mutations,
  actions
}
