import { Bar } from 'vue-chartjs';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from 'chart.js';
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);
export default {
  name: 'TaskPriorityChart',
  extends: Bar,
  props: {
    tasks: {
      type: Array,
      default: () => []
    }
  },
  mounted() {
    this.renderChart(this.chartData, this.chartOptions);
  },
  computed: {
    chartData() {
      const priorityCounts = {
        'Low': 0,
        'Medium': 0,
        'High': 0
      };
      this.tasks.forEach(task => {
        if (priorityCounts[task.priority] !== undefined) {
          priorityCounts[task.priority]++;
        }
      });
      return {
        labels: ['Low', 'Medium', 'High'],
        datasets: [{
          label: 'Tasks by Priority',
          data: [priorityCounts['Low'], priorityCounts['Medium'], priorityCounts['High']],
          backgroundColor: ['rgba(184, 216, 242, 0.7)',
          // Pastel blue
          'rgba(255, 229, 212, 0.7)',
          // Pastel peach
          'rgba(255, 209, 220, 0.7)' // Pastel pink
          ],
          borderColor: ['rgba(184, 216, 242, 1)', 'rgba(255, 229, 212, 1)', 'rgba(255, 209, 220, 1)'],
          borderWidth: 2,
          borderRadius: 8,
          borderSkipped: false
        }]
      };
    },
    chartOptions() {
      return {
        responsive: true,
        maintainAspectRatio: false,
        plugins: {
          legend: {
            display: false
          },
          tooltip: {
            backgroundColor: 'rgba(255, 255, 255, 0.9)',
            titleColor: '#333',
            bodyColor: '#666',
            borderColor: '#D4C5F9',
            borderWidth: 1,
            padding: 12,
            cornerRadius: 8,
            titleFont: {
              size: 14,
              weight: 'bold'
            },
            bodyFont: {
              size: 13
            }
          }
        },
        scales: {
          y: {
            beginAtZero: true,
            ticks: {
              stepSize: 1,
              font: {
                size: 11
              }
            },
            grid: {
              color: 'rgba(212, 197, 249, 0.1)'
            }
          },
          x: {
            grid: {
              display: false
            },
            ticks: {
              font: {
                size: 11,
                weight: 'bold'
              }
            }
          }
        }
      };
    }
  },
  watch: {
    tasks: {
      deep: true,
      handler() {
        this.renderChart(this.chartData, this.chartOptions);
      }
    }
  }
};
